  "÷": (firstVal, secondVal) => firstVal / secondVal,
  "×": (fisrtVal, secondVal) => fisrtVal * secondVal,
  "+": (fisrtVal, secondVal) => fisrtVal + secondVal,
  "-": (firstVal, secondVal) => firstVal - secondVal,
  "=": (firstVal, secondVal) => secondVal,
};

